import React, { Component } from "react";
// import API from "../../utils/API";
import axios from "axios";
import Wrapper from "../../components/Wrapper";
import Header from "../../components/Header";
import Query from "../../components/Query";
import Results from "../../components/Results";

class Home extends Component {
  //set initial state
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      topic: "",
      start: "",
      end: ""
    };
  }

  // When this component mounts, search for the article
  componentDidMount() {
    this.getArticle();
  }

  // Handles NYT API Function
  getArticle = query => {
    API.search(query)
      .then(response => {
        var results;
        console.log('response', response);
        results = response.data.response.docs;
        if (response.data.response.docs.length === 0) {
          results = [
            { headline: { main: "No Results" }, byline: { original: "" } }
          ];
        }
        this.setState({ articles: results });
      });
  };

  handleKeyPress = (e) => {
    if (e.key === "Enter") {
      this.handleChange(e.target.value);
      console.log(e.target.value);
    }
  };

  handleChange = (term) => {
    let newInput = term;
    if (newInput) {
      this.state.topic = newInput;
      this.getArticle();
    }
  };

  render() {
    return (
      <div>
        <Wrapper>
          <Header />
          <Query />
          {/* <form name="application">
            <fieldset>
              <legend>NYT Scraper</legend>
              <div>
                <label for="topic">Topic </label>
                <input onChange={this.handleFormInput} name="topic" placeholder="Topic" value=""></input>
              </div>
            </fieldset>
            <button className="submit" onClick={this.handleFormSubmit}>Submit</button>
          </form> */}
          <label for="topic">
          Topic
            <input
              className="form-control"
              name="topic"
              onKeyPress={this.handleKeyPress}
              ref="searcher"
              placeholder="Topic"
            />
          </label>
        <Results className="articles" articles={this.state.articles} />
        </Wrapper>
      </div>
    );
  }
}

export default Home;
