import React, { Component } from "react";
// import API from "../../utils/API";
import axios from "axios";
import Wrapper from "../../components/Wrapper";
import Header from "../../components/Header";
// import Query from "../../components/Query";
import Results from "../../components/Results";

class Home extends Component {
  //set initial state
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      topic: "",
      start: "",
      end: ""
    };
  }

  // Handles NYT API Function
  getArticle = () => {
    const apiURL = "https://api.nytimes.com/svc/";
    const apiKey = "d8fa6745e8a645958ac6b24539fa497f";
    let specialtyURL = "search/v2/articlesearch.json?q=";

    axios
      .get(
        apiURL +
          specialtyURL +
          this.state.topic +
          "&api-key=" + apiKey
      )
      .then(response => {
        var results;
        console.log('response', response);
        results = response.data.response.docs;
        if (response.data.response.docs.length === 0) {
          results = [
            { headline: { main: "No Results" }, byline: { original: "" } }
          ];
        }
        this.setState({ articles: results });
        this.clearForm();
      });
  };

  clearForm = () => {
    this.refs.searcher.value = "";
    this.setState({ topic: "" });
  };

  handleKeyPress = (e) => {
    if (e.key === "Enter") {
      this.handleChange(e.target.value);
      console.log(e.target.value);
    }
  };

  handleChange = (term) => {
    let newInput = term;
    if (newInput) {
      this.state.topic = newInput;
      this.getArticle();
    }
  };

  // From Jayson class example
  // handleFormInput = (event) => {
  //   const {name, value} = event.target;
  //   this.setState({
  //     [name]: value
  //   });
  // };

  handleFormSubmit = event => {
    event.preventDefault();
    this.handleChange(event.target.value);
    // saveForm(this.state);
    // alert(`Topic: ${this.state.topic}\nStart: ${this.state.start}\nEnd: ${this.state.end}`);
    // this.setState({ topic: "", start: "", end: "" });
  };

  render() {
    return (
      <div>
        <Wrapper>
          <Header />
          {/* <Query /> */}
          {/* <form name="application">
            <fieldset>
              <legend>NYT Scraper</legend>
              <div>
                <label for="topic">Topic </label>
                <input onChange={this.handleFormInput} name="topic" placeholder="Topic" value=""></input>
              </div>
            </fieldset>
            <button className="submit" onClick={this.handleFormSubmit}>Submit</button>
          </form> */}
          <input
            className="form-control"
            onKeyPress={this.handleKeyPress}
            ref="searcher"
            placeholder="Topic"
          />
        <Results className="articles" articles={this.state.articles} />
        </Wrapper>
      </div>
    );
  }
}

export default Home;
