import React, { Component } from "react";
// import API from "../../utils/API";
import axios from "axios";
import Wrapper from "../../components/Wrapper";
import Header from "../../components/Header";
import Query from "../../components/Query";
// import { Input, FormBtn } from "../../components/Form";
// import Results from "../../components/Results";

class Articles extends React.Component{
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div className="card">
        <h3 className="card-header">Results</h3>
        <div className="card-body">
          <ul>
            {this.props.articles.map(function(article) {
              return (
                <li key={article._id}>
                  <a href={article.web_url} target="_blank">
                    <span className="bold">{article.headline.main}</span> --
                    {" "}{article.byline.original}
                  </a>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    );
  }
}

class Home extends Component {
  //set initial state
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      topic: "",
      start: "",
      end: ""
    };
  }

  // Handles NYT API Function

  getArticle = () => {
    const apiURL = "https://api.nytimes.com/svc/";
    const apiKey = "d8fa6745e8a645958ac6b24539fa497f";
    let specialtyURL = "search/v2/articlesearch.json?q=";

    axios
      .get(
        apiURL +
          specialtyURL +
          this.state.topic +
          "&api-key=" + apiKey
      )
      .then(response => {
        var results;
        console.log('response', response);
        results = response.data.response.docs;
        if (response.data.response.docs.length === 0) {
          results = [
            { headline: { main: "No Results" }, byline: { original: "" } }
          ];
        }
        this.setState({ articles: results });
        this.clearForm();
      });
  };

  clearForm = () => {
    this.refs.searcher.value = "";
    this.setState({ topic: "" });
  };

  handleKeyPress = (e) => {
    if (e.key === "Enter") {
      this.handleChange(e.target.value);
      console.log(e.target.value);
    }
  };

  handleChange = (term) => {
    let newInput = term;
    if (newInput) {
      this.state.topic = newInput;
      this.getArticle();
    }
  };

  // handleFormSubmit = event => {
  //   event.preventDefault();
  //   alert(`Topic: ${this.state.topic}\nStart: ${this.state.start}\nEnd: ${this.state.end}`);
  //   this.setState({ topic: "", start: "", end: "" });
  // };

  render() {
    return (
      <div>
        <Wrapper>
          <Header />
          <Query
            onChange={this.handleKeyPress}
          />
        <Articles className="articles" articles={this.state.articles} />
        </Wrapper>
      </div>
    );
  }
}

export default Home;
