class Home extends Component {
  //set initial state
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      topic: "",
      start: "",
      end: ""
    };
  }



  // Handles NYT API Function
  getArticle = () => {
    const apiURL = "https://api.nytimes.com/svc/";
    const apiKey = "d8fa6745e8a645958ac6b24539fa497f";
    let specialtyURL = "search/v2/articlesearch.json?q=";

    axios
      .get(
        apiURL +
          specialtyURL +
          this.state.topic +
          "&api-key=" + apiKey
      )
      .then(response => {
        var results;
        console.log('response', response);
        results = response.data.response.docs;
        if (response.data.response.docs.length === 0) {
          results = [
            { headline: { main: "No Results" }, byline: { original: "" } }
          ];
        }
        this.setState({ articles: results });
        this.clearForm();
      });
  };

  clearForm = () => {
    this.refs.searcher.value = "";
    this.setState({ topic: "" });
  };

  handleKeyPress = (e) => {
    if (e.key === "Enter") {
      this.handleChange(e.target.value);
      console.log(e.target.value);
    }
  };

  handleChange = (term) => {
    let newInput = term;
    if (newInput) {
      this.state.topic = newInput;
      this.getArticle();
    }
  };

  handleFormSubmit = event => {
    event.preventDefault();
    this.handleChange(event.target.value);
    // alert(`Topic: ${this.state.topic}\nStart: ${this.state.start}\nEnd: ${this.state.end}`);
    // this.setState({ topic: "", start: "", end: "" });
  };

  render() {
    return (
      <div>
        <Wrapper>
          <Header />
          {/* <Query /> */}
          <input
            className="form-control"
            onKeyPress={this.handleKeyPress}
            ref="searcher"
            placeholder="Topic"
          />
        <Articles className="articles" articles={this.state.articles} />
        </Wrapper>
      </div>
    );
  }
}

export default Home;
