import React, { Component } from "react";
// import API from "../../utils/API";
import Wrapper from "../../components/Wrapper";
import Header from "../../components/Header";
import Query from "../../components/Query";
import { Input, FormBtn } from "../../components/Form";
import Results from "../../components/Results";
import API from "../../utils/API";

class Home extends Component {
  state = {
    articles: [],
    topic: "",
    start: "",
    end: ""
  };

  // When this component mounts, search for the movie "The Matrix"
  componentDidMount() {
    this.searchArticles("trump");
  }

  searchArticles = query => {
    API.search(query)
      .then(res => this.setState({ result: res.data }))
      .catch(err => console.log(err));
  };

  handleFormSubmit = event => {
    event.preventDefault();
    alert(`Topic: ${this.state.topic}\nStart: ${this.state.start}\nEnd: ${this.state.end}`);
    this.setState({ topic: "", start: "", end: "" });
  };

  render() {
    return (
      <div>
        <Wrapper>
          <Header />
          <form>
              <Input
                type="text"
                value={this.state.topic}
                onChange={this.handleInputChange}
                name="topic"
                placeholder="Topic(required)"
              />
              <Input
                type="text"                
                value={this.state.start}
                onChange={this.handleInputChange}
                name="start"
                placeholder="YYYYMMDD (Optional)"
              />
              <Input
                type="text"                
                value={this.state.end}
                onChange={this.handleInputChange}
                name="end"
                placeholder="YYYYMMDD (Optional)"
              />
              <FormBtn
                disabled={!(this.state.topic && this.state.start && this.state.end)}
                onClick={this.handleFormSubmit}
              >
                Submit
              </FormBtn>
            </form>
          <Results />
        </Wrapper>
      </div>
    );
  }
}

export default Home;
