import React, { Component } from "react";
import API from "../../utils/API";
import Wrapper from "../../components/Wrapper";
import Header from "../../components/Header";
import Query from "../../components/Query";
import Results from "../../components/Results";

class Home extends Component {
  //Set initial state
  state = {
      articles: [],
      topic: "",
      // start: "",
      // end: ""
  };

  // When this component mounts, search for the article
  // componentDidMount() {
  //   this.getArticle();
  // }

  // Handles NYT API Function
  getArticle = query => {
    API.search(query)
      .then(response => {
        var results;
        console.log('response', response);
        results = response.data.response.docs;
        if (response.data.response.docs.length === 0) {
          results = [
            { headline: { main: "No Results" }, byline: { original: "" } }
          ];
        }
        this.setState({ articles: results });
      });
  };

  handleInputChange = event => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
      [name]: value
    });
  };


  // When the form is submitted, search the NYT API for the value of `this.state.topic`
  handleFormSubmit = event => {
    event.preventDefault();
    this.getArticle(this.state.topic);
  };


  render() {
    return (
      <div>
        <Wrapper>
          <Header />
          {/* <Query /> */}
          {/* <form name="application">
            <fieldset>
              <legend>NYT Scraper</legend>
              <div>
                <label for="topic">Topic </label>
                <input onChange={this.handleFormInput} name="topic" placeholder="Topic" value=""></input>
              </div>
            </fieldset>
            <button className="submit" onClick={this.handleFormSubmit}>Submit</button>
          </form> */}
          {/* <label for="topic">
          Topic
            <input
              className="form-control"
              name="topic"
              onKeyPress={this.handleKeyPress}
              ref="searcher"
              placeholder="Topic"
            />
          </label> */}
          <Query
            value={this.state.topic}
            handleInputChange={this.handleInputChange}
            handleFormSubmit={this.handleFormSubmit}
          />
        <Results className="articles" articles={this.state.articles} />
        </Wrapper>
      </div>
    );
  }
}

export default Home;
