import React, { Component } from "react";
// import API from "../../utils/API";
import axios from "axios";
import Wrapper from "../../components/Wrapper";
import Header from "../../components/Header";
import Query from "../../components/Query";
// import { Input, FormBtn } from "../../components/Form";
import Results from "../../components/Results";

class Home extends Component {
  state = {
    articles: [],
    topic: "",
    start: "",
    end: ""
  };

  // Handles NYT API Function

  getArticle = () => {
    const apiURL = "https://api.nytimes.com/svc/";
    const apiKey = "d8fa6745e8a645958ac6b24539fa497f";
    let specialtyURL = "search/v2/articlesearch.json?q=";

    axios
      .get(
        apiURL +
          specialtyURL +
          this.state.userInput +
          "&api-key=" +
          this.state.apiKey
      )
      .then(response => {
        var results;
        console.log('response', response);
        if (this.state.searchType === "articles") {
           results = response.data.response.docs;
          if (response.data.response.docs.length === 0) {
            results = [
              { headline: { main: "No Results" }, byline: { original: "" } }
            ];
          }
          this.setState({ articles: results });
        }
      });
  };


  handleFormSubmit = event => {
    event.preventDefault();
    alert(`Topic: ${this.state.topic}\nStart: ${this.state.start}\nEnd: ${this.state.end}`);
    this.setState({ topic: "", start: "", end: "" });
  };

  render() {
    return (
      <div>
        <Wrapper>
          <Header />
          <Query />
          <Results />
        </Wrapper>
      </div>
    );
  }
}

export default Home;
